mapper.select:
SELECT COUNT(*) 
FROM tbl_score
WHERE sc_stnum = #{sc_stnum}

SELECT Sum(sc_score) 
FROM tbl_score
WHERE sc_stnum = #{sc_stnum}

mapper.update:
UDPATE tbl_Score SET sc_stnum
= #{sc_stnum}
sc_stnum = #{sc_stnum},
sc_sbcode = #{sc_sbcode},
sc_score
= #{sc_score}
WHERE sc_seq =#{sc_seq}

mapper.insert:
INSERT INTO tbl_Score(
sc_stnum, sc_sbcode, sc_score
)
VALUES(
#{sc_stnum},#{sc_sbcode},#{sc_score}
)
